import { addScbCars, delScbCars, getScbCars, listScbCars, updateScbCars } from '@/api/scbcars'
import { getAttendants } from '@/api/scbteachers'
import '@riophae/vue-treeselect/dist/vue-treeselect.css'
export default {
  name: 'Scbcars',
  data() {
    return {
      attendantsOptions: [],
      // 遮罩层
      loading: true,
      // 选中数组
      ids: [],
      // 非单个禁用
      single: true,
      // 非多个禁用
      multiple: true,
      // 总条数
      total: 0,
      // 弹出层标题
      title: '',
      // 是否显示弹出层
      open: false,
      isEdit: false,
      // 类型数据字典
      typeOptions: [],
      scbcarsList: [],

      // 查询参数
      queryParams: {
        pageIndex: 1,
        pageSize: 10,
        carNumber:
            undefined,
        carNo:
            undefined,
        driver:
            undefined,
        phone:
            undefined

      },
      // 表单参数
      form: {
        deptId: undefined,
        deptName: undefined
      },
      // 表单校验
      rules: { carNumber:
                [
                  { required: true, message: '车牌编号不能为空', trigger: 'blur' }
                ],
      carNo:
                [
                  { required: true, message: '车牌号不能为空', trigger: 'blur' }
                ],
      driver:
                [
                  { required: true, message: '司机不能为空', trigger: 'blur' }
                ],
      phone:
                [
                  { required: true, message: '手机号不能为空', trigger: 'blur' }
                ]
      }
    }
  },
  created() {
    this.getList()
  },
  methods: {
    normalizer(node) {
      if (node.children && !node.children.length) {
        delete node.children
      }
      return {
        id: node.id,
        label: node.name,
        children: node.children
      }
    },
    /** 查询参数列表 */
    getList() {
      this.loading = true
      listScbCars(this.addDateRange(this.queryParams, this.dateRange)).then(response => {
        this.scbcarsList = response.data.list
        this.total = response.data.count
        this.loading = false
      }
      )
    },
    /** 查询班级下拉树结构 */
    getTreeselect(e) {
      getAttendants().then(response => {
        this.attendantsOptions = []
        for (var i = 0; i < response.data.length; i++) {
          const d = response.data[i]
          const formatData = {
            'label': d.name,
            'value': d.id
          }
          this.attendantsOptions.push(formatData)
        }
      })
    },
    // 取消按钮
    cancel() {
      this.open = false
      this.reset()
    },
    // 表单重置
    reset() {
      this.form = {

        id: undefined,
        carNumber: undefined,
        carNo: undefined,
        seatsNum: undefined,
        attendantId: undefined,
        driver: undefined,
        phone: undefined,
        dept: undefined,
        isDelete: undefined,
        deptId: undefined,
        deptName: undefined
      }
      this.resetForm('form')
    },

    /** 搜索按钮操作 */
    handleQuery() {
      this.queryParams.pageIndex = 1
      this.getList()
    },
    /** 重置按钮操作 */
    resetQuery() {
      this.dateRange = []
      this.resetForm('queryForm')
      this.handleQuery()
    },
    /** 新增按钮操作 */
    handleAdd() {
      this.reset()
      this.open = true
      this.title = '添加车辆'
      this.isEdit = false

      this.getTreeselect('add')
    },
    // 多选框选中数据
    handleSelectionChange(selection) {
      this.ids = selection.map(item => item.row)
      this.single = selection.length !== 1
      this.multiple = !selection.length
    },
    /** 修改按钮操作 */
    handleUpdate(row) {
      this.reset()
      const id = row.id
      getScbCars(id).then(response => {
        this.form = response.data
        this.open = true
        this.title = '修改车辆'
        this.isEdit = true
      })
      this.getTreeselect('update')
    },
    /** 提交按钮 */
    submitForm: function() {
      this.$refs['form'].validate(valid => {
        if (valid) {
          if (this.form.id !== undefined) {
            updateScbCars(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('修改成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          } else {
            addScbCars(this.form).then(response => {
              if (response.code === 200) {
                this.msgSuccess('新增成功')
                this.open = false
                this.getList()
              } else {
                this.msgError(response.msg)
              }
            })
          }
        }
      })
    },
    /** 删除按钮操作 */
    handleDelete(row) {
      const Ids = row.id || this.ids
      this.$confirm('是否确认删除编号为"' + Ids + '"的数据项?', '警告', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function() {
        return delScbCars(Ids)
      }).then(() => {
        this.getList()
        this.msgSuccess('删除成功')
      }).catch(function() {
      })
    }
  }
}
